import React, { useState } from "react";
import Slide from "./Slide.jsx";
import "./Carousel.css"; // Include your CSS file
import ProductBanners from "./ProductBanners";

const Carousel = () => {
  const [activeSlide, setActiveSlide] = useState(0);

  const handlePrev = () => {
    setActiveSlide((prevSlide) => Math.max(0, prevSlide - 1));
  };

  const handleNext = () => {
    setActiveSlide((prevSlide) =>
      Math.min(ProductBanners.length - 1, prevSlide + 1)
    );
  };

  return (
    <div className="carousel_container">
      <div className="buttons">
        <div className="left_arrow" onClick={handlePrev}>
          {/* Left arrow SVG */}
        </div>
        <div className="right_arrow" onClick={handleNext}>
          {/* Right arrow SVG */}
        </div>
        <div className="radio_button">
          {ProductBanners.map((item, _) => (
            <div className="radio">
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="16"
                height="16"
                fill="currentColor"
                className="bi bi-dot"
                viewBox="0 0 16 16"
              >
                <path d="M8 9.5a1.5 1.5 0 1 0 0-3 1.5 1.5 0 0 0 0 3" />
              </svg>
            </div>
          ))}
        </div>
      </div>

      {ProductBanners.map((item, idx) => (
        <Slide
          key={item.id}
          idx={idx}
          title={item.title}
          url={item.url}
          cN={idx !== activeSlide ? "slide_notactive" : "slide"}
        />
      ))}
    </div>
  );
};

export default Carousel;
